import React, { useState, useEffect } from 'react';
import './main-app-screen.scss';
import apiRequests from '../utils/apiRequests';

const MainAppScreen = (props) => {

    const { selectRoster } = props;

    useEffect(() => {
        async function fetchRosters() {
        try {
            const response = await axios.get(`${origin}/api/v1/rosters`);
            setTeams(response.data.rows[1].team_name)
        } catch(err) {
            console.error(err) 
            }
        }
        fetchRosters();

    })

    const getRosterSelectionPanel = () => (
        <RosterSelectionPanel
          submitRoster={(code) => {
            selectRoster(code);
            setAppState(appStatus.WELCOME_PANEL);
          }}
        />
      );

    const MainPanel = getMainPanel();

    return (
        <div className="dashboardContainer">
          <Header submitRoster={changeRoster} restartApp={restartApp} goBack={goBack} />
          {usernameDisplay}
          {MainPanel}
          <div className="footerPadding" />
          <Footer />
        </div>
      );
}

export default MainAppScreen;
